                    return;
                }
    
                if (!String.Equals(context.Shape.EditorFlavor, "html", StringComparison.InvariantCultureIgnoreCase)) {
                    return;
                }
                
                _resourceManager.Require("script", "FixedTinyMce").AtFoot();
            }
        }

